changeSet.sql:
create table if not exists medicine_unit ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table if not exists medicine_category ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table if not exists medicine ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100), category_id bigint, unit_id bigint, info varchar(100), constraint medicine_category_id_fk foreign key (category_id) references medicine_category (id), constraint medicine_unit_id_fk foreign key (unit_id) references medicine_unit (id) );
