changeSet.sql:
create table if not exists medicine_unit ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table if not exists medicine_category ( id bigint primary key auto_increment, active boolean, arabic_name varchar(100), english_name varchar(100) );
create table if not exists medicine ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(255), english_name varchar(255), category_id bigint, unit_id bigint, info varchar(100) );
create table if not exists medicine_donation( id bigint auto_increment primary key, active tinyint(1) default 1 null, title varchar(100) null, description varchar(600) null, category varchar(50) null, status varchar(50) null, communication_method varchar(50) null, donation_date datetime null, donation_expiration_date datetime null, medicine_id bigint null, quantity int null, medicine_unit_id bigint null, medicine_expiration_date datetime null, user_id bigint null, city_id bigint null );
