changeSet.sql:
create table if not exists food_category ( id bigint primary key auto_increment, active boolean default true, arabic_name varchar(50), english_name text(50), description varchar(150) );
create table if not exists food_unit ( id bigint primary key auto_increment, active boolean default true, arabic_name varchar(50), english_name text(50), description varchar(150) );
create table if not exists food_donation ( id bigint primary key auto_increment, active boolean default true, title varchar(100), description varchar(800), donation_date datetime, donation_expiration_date datetime, food_expiration_date datetime, status varchar(50), category varchar(50), communication_method varchar(50), amount float, unit_id bigint, category_id bigint, user_id bigint, city_id bigint, constraint foreign key (user_id) references user_ (id), constraint foreign key (city_id) references city (id), constraint foreign key (unit_id) references food_unit (id), constraint foreign key (category_id) references food_category (id) );
