changeSet.sql:
create table user_ ( id bigint auto_increment primary key, active boolean default 1, first_name varchar(255), last_name varchar(255), phone_number varchar(255), email varchar(255), password varchar(255), uid varchar(100), username varchar(100), device_token varchar(500) );
create table state ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100), country_id bigint );
create table if not exists city ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100), state_id bigint );
