changeSet.sql:
create table clothing_condition ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table clothing_category ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table clothing_size ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table clothing_season ( id bigint primary key auto_increment, active boolean default 1, arabic_name varchar(100), english_name varchar(100) );
create table clothing_donation ( id bigint primary key auto_increment, active boolean default true, title varchar(100), description varchar(600), category varchar(50), communication_method varchar(50), status varchar(50), donation_date datetime, donation_expiration_date datetime, quantity int, clothing_condition_id bigint, clothing_size_id bigint, clothing_category_id bigint, clothing_season_id bigint, user_id bigint, city_id bigint );
